import React, { useState, useEffect, useRef } from "react";
import moment from "moment";
import "moment/locale/ko";
import More from "./../img/icon/icMore.png";
import Bin from "./../img/icon/icBin.png";
import Comment from "./../img/icon/icChat.png";
import AnonymousAnswer from "./AnonymousAnswer";
import Delete from "./popup/QDelete";
import { getPReceivedComment } from "../api/Q&A/getPReceivedComment";
import { getSpaceInfo } from "../api/getSpaceInfo";
import UntilAnswering from "./UntilAnswering";
import AnswerRegister from "./popup/AnswerRegister";
import CantModal from "./popup/CantRegister";
import ADelete from "./popup/ADelete";
import AnswerBtn from "./AnswerButton";
import Profile5 from "./../img/Ellipse 105.png";
import Profile2 from "./../img/Ellipse 104.png";
import Loading from "./Loading";

function ReceiveComment({ spaceId, currentUserInfo }) {
  const [page, setPage] = useState(0); // ÌéòÏù¥ÏßÄ Î≤àÌò∏ ÏÉÅÌÉúÍ∞í Ï∂îÍ∞Ä
  const [pageSize, setPageSize] = useState(5); // ÌéòÏù¥ÏßÄ ÌÅ¨Í∏∞ ÏÉÅÌÉúÍ∞í Ï∂îÍ∞Ä
  const [loading, setLoading] = useState(false); // Î°úÎî© ÏÉÅÌÉúÍ∞í Ï∂îÍ∞Ä
  const [allDataFetched, setAllDataFetched] = useState(false); // Î™®Îì† Îç∞Ïù¥ÌÑ∞Î•º Í∞ÄÏ†∏ÏôîÎäîÏßÄ Ïó¨Î∂ÄÎ•º ÎÇòÌÉÄÎÇ¥Îäî ÏÉÅÌÉúÍ∞í Ï∂îÍ∞Ä
  const [fetchingMoreData, setFetchingMoreData] = useState(false);
  const [receivedComments, setReceivedComments] = useState([]);
  // ÏßàÎ¨∏ ÏÇ≠Ï†ú ÏÉÅÌÉúÍ∞í
  const [deleteStates, setDeleteStates] = useState([]);
  // ÎãµÎ≥Ä ÏÇ≠Ï†ú ÏÉÅÌÉúÍ∞í
  const [a_deleteStates, a_setDeleteStates] = useState([]);

  // ÏßàÎ¨∏ Í≥µÏú† ÏÉÅÌÉúÍ∞í
  const [shareStates, setShareStates] = useState({});

  //ÏßàÎ¨∏ ÏÇ≠Ï†ú Î™®Îã¨ Ïò§Ìîà ÏÉÅÌÉúÍ∞í
  const [delModal, setDelModal] = useState(false);
  //ÎãµÎ≥Ä ÏÇ≠Ï†ú Î™®Îã¨ Ïò§Ìîà ÏÉÅÌÉúÍ∞í
  const [a_delModal, a_setDelModal] = useState(false);
  //Îì±Î°ùÎ∂àÍ∞Ä Î™®Îã¨ Ïò§Ìîà ÏÉÅÌÉúÍ∞í
  const [cantModal, setCantModal] = useState(false);
  const [answerModal, setAnswerModal] = useState(false);

  // ÏÑ†ÌÉùÌïú ÎãµÎ≥ÄÏùò Í≥†Ïú† IDÎ•º ÏÉÅÌÉúÍ∞íÏóê Ï†ÄÏû•
  const [selectedAnswerId, setSelectedAnswerId] = useState([]);

  // ÏÑ†ÌÉùÌïú ÏßàÎ¨∏Ïùò Í≥†Ïú† IDÎ•º ÏÉÅÌÉúÍ∞íÏóê Ï†ÄÏû•
  const [selectedQuestionId, setSelectedQuestionId] = useState([]);

  const [selectedQuestionUserId, setSelectedQuestionUserId] = useState([]);
  const [selectedQuestionUserPic, setSelectedQuestionPic] = useState([]);
  const [selectedQuestionText, setSelectedQuestionText] = useState([]);

  // ÏÑ†ÌÉùÌïú ÏßàÎ¨∏Ïùò Ïä§ÌéòÏù¥Ïä§ IDÏôÄ Ïú†Ï†Ä IDÎ•º ÏÉÅÌÉúÍ∞íÏóê Ï†ÄÏû•
  const [selectedSpaceId, setSelectedSpaceId] = useState([]);
  const [selectedUserId, setSelectedUserId] = useState([]);

  // More ÏïÑÏù¥ÏΩò ÎìúÎ°≠Îã§Ïö¥ Ïò§Ìîà Ïó¨Î∂Ä ÏÉÅÌÉúÍ∞í
  const [q_openedDropdownIndex, q_setOpenedDropdownIndex] = useState([]);
  const [a_openedDropdownIndex, a_setOpenedDropdownIndex] = useState([]);

  // Ïù¥ÎèôÌïú Ïä§ÌéòÏù¥Ïä§ ÏÉÅÌÉúÍ∞í
  const [spaceOwner, setSpaceOwner] = useState({
    userId: "",
    picture: "",
    name: "",
  });

  const fetchData = async (isInitialFetch = true) => {
    try {
      setLoading(true);

      // isInitialFetchÍ∞Ä trueÏùº Í≤ΩÏö∞ÏóêÎßå ÌéòÏù¥ÏßÄ Î≤àÌò∏Î•º Ï¥àÍ∏∞Ìôî
      if (isInitialFetch) {
        setPage(0);
      }

      const spaInfo = await getSpaceInfo(spaceId);
      const response = await getPReceivedComment(spaceId, page, pageSize);


      //Î≠êÍ∞Ä undefinedÏù∏ÏßÄ ÌôïÏù∏ÌïòÍ∏∞ ÏúÑÌïú Î°úÍ∑∏
      console.log("response: ",response.data);
      console.log("receivedComments: ",receivedComments);

     // newCommentsÍ∞Ä Ï†ïÏùòÎêòÏßÄ ÏïäÏïòÎã§Î©¥ Îπà Î∞∞Ïó¥Î°ú Ï¥àÍ∏∞Ìôî
      const newComments = isInitialFetch
        ? response.data
        : [...receivedComments, ...response.data];
      setReceivedComments(newComments);
      setSpaceOwner(spaInfo);

      // deleteStates Î∞∞Ïó¥ÏùÑ Î™®Îì† ÏßàÎ¨∏Ïóê ÎåÄÌï¥ Ï¥àÍ∏∞Ìôî
      // fix: newCommentsÏùò undefined ÏùÑ Îß§ÌïëÌïòÎäî Î¨∏Ï†ú Ìï¥Í≤∞
      const initialDeleteStates = newComments.map(() => false);

      setDeleteStates(initialDeleteStates);
      a_setDeleteStates(initialDeleteStates);
      setShareStates(initialDeleteStates);

      // Î™®Îì† Îç∞Ïù¥ÌÑ∞Î•º Î∂àÎü¨Ïò® Í≤ΩÏö∞ÏóêÎäî Îçî Ïù¥ÏÉÅ Îç∞Ïù¥ÌÑ∞Î•º Î∂àÎü¨Ïò§ÏßÄ ÏïäÎèÑÎ°ù ÏÑ§Ï†ï
      if (response.data.length === 0) {
        setAllDataFetched(true);
      }

      setLoading(false);
    } catch (error) {
      setLoading(false);
      console.error("Error fetching data:", error);
    }
  };

  const spinnerRef = useRef(null);

  useEffect(() => {
    fetchData();
  }, [spaceId, pageSize]);

  useEffect(() => {
    const fetchDataOnScroll = async () => {
      if (!fetchingMoreData && !allDataFetched) {
        setFetchingMoreData(true);

        const response = await getPReceivedComment(spaceId, page + 1, pageSize);

        setFetchingMoreData(false);

        if (response.data.length === 0) {
          setAllDataFetched(true);
          return; // Îçî Ïù¥ÏÉÅ Îç∞Ïù¥ÌÑ∞Î•º Î∂àÎü¨Ïò¨ ÌïÑÏöîÍ∞Ä ÏóÜÏúºÎØÄÎ°ú Ï¢ÖÎ£å
        }

        setReceivedComments((prevData) => [
          ...prevData,
          ...response.data.map((item) => ({ ...item, key: item.id })),
        ]);
        setPage((prevPage) => prevPage + 1);
      }
    };

    const handleScroll = () => {
      const scrollY = window.scrollY;
      const windowHeight = window.innerHeight;
      const contentHeight = document.body.scrollHeight;

      if (scrollY >= contentHeight - windowHeight - 200) {
        fetchDataOnScroll();
      }
    };

    window.addEventListener("scroll", handleScroll);

    return () => {
      window.removeEventListener("scroll", handleScroll);
    };
  }, [spaceId, page, pageSize, allDataFetched, fetchingMoreData]);

  // ÌÅ¥Î¶≠Ìïú ÏßàÎ¨∏Ïóê ÎåÄÌïú ÏÇ≠Ï†ú ÏÉÅÌÉúÍ∞í Î≥ÄÍ≤Ω
  const clickMore = (index) => {
    q_setOpenedDropdownIndex((prevIndex) =>
      prevIndex === index ? null : index
    );
    a_setOpenedDropdownIndex(null); // Close answer modal if any is open
    setDeleteStates((prevStates) => {
      const newStates = [...prevStates];
      newStates[index] = !newStates[index];
      return newStates;
    });
  };

  // ÌÅ¥Î¶≠Ìïú ÎãµÎ≥ÄÏóê ÎåÄÌïú ÏÇ≠Ï†ú ÏÉÅÌÉúÍ∞í Î≥ÄÍ≤Ω
  const clickMore_1 = (index) => {
    a_setOpenedDropdownIndex((prevIndex) =>
      prevIndex === index ? null : index
    );
    q_setOpenedDropdownIndex(null); // Close question modal if any is open
    a_setDeleteStates((prevStates) => {
      const newStates = [...prevStates];
      newStates[index] = !newStates[index];
      return newStates;
    });
  };

  // ÌÅ¥Î¶≠Ìïú ÏßàÎ¨∏Ïóê ÎåÄÌïú Í≥µÏú†ÌïòÍ∏∞ ÏÉÅÌÉúÍ∞í Î≥ÄÍ≤Ω
  const clickMore_s = (index) => {
    setShareStates((prevStates) => {
      const newStates = [...prevStates];
      newStates[index] = !newStates[index];
      return newStates;
    });
  };

  // ÏßàÎ¨∏ ÏÇ≠Ï†úÌïòÍ∏∞ ÌÅ¥Î¶≠ Ïãú Î™®Îã¨ Ïò§Ìîà
  const showDelModal = (questionId, spaceId, userId) => {
    // ÏÇ≠Ï†úÌïòÍ∏∞ ÎìúÎûçÎã§Ïö¥ ÏßÄÏö∞Í∏∞
    setDeleteStates("");
    setSelectedQuestionId(questionId); // ÏÑ†ÌÉùÌïú ÏßàÎ¨∏Ïùò IDÎ•º ÏÉÅÌÉúÍ∞íÏóê Ï†ÄÏû•
    setSelectedSpaceId(spaceId); // ÏÑ†ÌÉùÌïú ÏßàÎ¨∏Ïùò Ïä§ÌéòÏù¥Ïä§ IDÎ•º ÏÉÅÌÉúÍ∞íÏóê Ï†ÄÏû•
    setSelectedUserId(userId); // ÏÑ†ÌÉùÌïú ÏßàÎ¨∏Ïùò Ïú†Ï†Ä IDÎ•º ÏÉÅÌÉúÍ∞íÏóê Ï†ÄÏû•
    setDelModal(true);
  };

  // ÎãµÎ≥Ä ÏÇ≠Ï†úÌïòÍ∏∞ ÌÅ¥Î¶≠ Ïãú Î™®Îã¨ Ïò§Ìîà
  const a_showDelModal = (answerId, spaceId, userId) => {
    setSelectedAnswerId(answerId); // ÏÑ†ÌÉùÌïú ÏßàÎ¨∏Ïùò IDÎ•º ÏÉÅÌÉúÍ∞íÏóê Ï†ÄÏû•
    setSelectedSpaceId(spaceId); // ÏÑ†ÌÉùÌïú ÏßàÎ¨∏Ïùò Ïä§ÌéòÏù¥Ïä§ IDÎ•º ÏÉÅÌÉúÍ∞íÏóê Ï†ÄÏû•
    setSelectedUserId(userId); // ÏÑ†ÌÉùÌïú ÏßàÎ¨∏Ïùò Ïú†Ï†Ä IDÎ•º ÏÉÅÌÉúÍ∞íÏóê Ï†ÄÏû•
    a_setDelModal(true);
  };

  // ÎëêÎ≤àÏß∏ ÎãµÎ≥Ä Îì±Î°ù Ïãú Î™®Îã¨ Ïò§Ìîà
  const showCantModal = () => {
    setCantModal(true);
  };

  // ÏÇ≠Ï†ú ÌåùÏóÖ  Îã´Í∏∞
  const onClose = (e) => {
    setDelModal(false);
    a_setDelModal(false);
    setCantModal(false);
    // setShare(false);
  };

  //Í≥µÏú†ÌïòÍ∏∞  Ïò§Ìîà
  // const showShareModal = (questionId) => {
  //   setSelectedQuestionId(questionId); // ÏÑ†ÌÉùÌïú ÏßàÎ¨∏Ïùò IDÎ•º ÏÉÅÌÉúÍ∞íÏóê Ï†ÄÏû•
  //   setShare(true);
  // };

  const showAnswerModal = (
    questionId,
    sentUserId,
    sentUserPic,
    questionText
  ) => {
    setAnswerModal(true);
    setSelectedQuestionId(questionId); // ÏÑ†ÌÉùÌïú ÏßàÎ¨∏Ïùò IDÎ•º ÏÉÅÌÉúÍ∞íÏóê Ï†ÄÏû•
    setSelectedQuestionUserId(sentUserId); // ÏÑ†ÌÉùÌïú ÏßàÎ¨∏Ïùò Ïú†Ï†Ä ÏïÑÏù¥ÎîîÎ•º ÏÉÅÌÉúÍ∞íÏóê Ï†ÄÏû•
    setSelectedQuestionPic(sentUserPic); // ÏÑ†ÌÉùÌïú ÏßàÎ¨∏Ïùò Ïú†Ï†Ä ÏÇ¨ÏßÑÍ∞íÏùÑ ÏÉÅÌÉúÍ∞íÏóê Ï†ÄÏû•
    setSelectedQuestionText(questionText); // ÏÑ†ÌÉùÌïú ÏßàÎ¨∏Ïùò ÎÇ¥Ïö© ÏÉÅÌÉúÍ∞íÏóê Ï†ÄÏû•
  };

  const closeAnswerModal = () => {
    setAnswerModal(false);
  };

  return (
    <>
      {receivedComments && receivedComments.length === 0 && (
        <>
         <div className="placeholder">
            <div className="profileArea">
              <img src={Profile5} alt="profile1" className="pre_questioner" />
            </div>
            <div className="cnt">
              <p className="pre_Nickname">ÏùµÎ™ÖÏùò ÌÜ†ÎÅº</p>
              <p className="pre_min">Ïñ∏Ï††Í∞Ä</p>
              <p className="pre_commentCnt">
                Î∞õÏùÄ ÏßàÎ¨∏Ïù¥ ÏóÜÏñ¥Ïöîü§ñ Ï≤´ Î¨¥Î¨ºÏùò Ï£ºÏù∏Í≥µÏù¥ ÎêòÏñ¥ Î≥¥ÏÑ∏Ïöî!
              </p>
            </div>
          </div>
        </>
      )}
      {receivedComments && receivedComments.slice().map((received, index) => (
        <React.Fragment key={received.id}>
          <div
            key={received.id}
            className={
              received.answers.length > 0
                ? "commentWrap questionWrap"
                : "pre"
            }
          >
            <div className="profileArea">
              <img
                src={received.sentUserPic}
                alt="profile1"
                className={`questioner ${
                  received.isAnonymous ? "anonymous" : ""
                }`}
                onClick={() => {
                  if (!received.isAnonymous) {
                    window.location.href = `/${received.sendingUserId}`;
                  }
                }}
              />
            </div>
            <div className="cnt">
              <p className="Nicname">{received.userId}</p>
              <p className="min">{getTimeDifference(received.createdTime)}</p>
              <p className="commentCnt">{received.questionText}</p>
              <div className="heart">
                {received.answers.length > 0 ? (
                  received.receivingUserId === currentUserInfo.userId && (
                    <img
                      src={Comment}
                      alt="comment"
                      className="chat"
                      onClick={showCantModal}
                    />
                  )
                ) : (
                  <>
                    {currentUserInfo.userId !== spaceOwner.userId ? (
                      ""
                    ) : (
                      <img
                        src={Comment}
                        alt="comment"
                        className="chat"
                        onClick={() =>
                          showAnswerModal(
                            received.id,
                            received.userId,
                            received.sentUserPic,
                            received.questionText
                          )
                        }
                      />
                    )}
                  </>
                )}
              </div>

              {
                // 1. ÎÇ¥ Ïä§ÌéòÏù¥Ïä§Ïùò ÏßàÎ¨∏Ïùº Îïå currentUserInfo.userId === spaceOwner.userId
                // 2. ÎÇ¥Í∞Ä ÏûëÏÑ±Ìïú ÏßàÎ¨∏, ÎãµÎ≥ÄÏùº Îïå currentUserInfo.userId === received.userId
              }
              <div className="more">
                {currentUserInfo.userId === spaceOwner.userId ||
                currentUserInfo.userId === received.sendingUserId ? (
                  <img src={More} alt="more" onClick={() => clickMore(index)} />
                ) : (
                  ""
                )}
                {deleteStates[index] && (
                  <div
                    className="del"
                    onClick={() =>
                      showDelModal(received.id, spaceId, currentUserInfo.userId)
                    }
                  >
                    <p>
                      <img src={Bin} alt="btin" />
                      ÏÇ≠Ï†úÌïòÍ∏∞
                    </p>
                  </div>
                )}
              </div>
            </div>
          </div>

          <div className="commentWrap answerWrap">
            <div className="profileArea">
              {received.answers.length === 0 ? (
                <img
                  src={spaceOwner.picture}
                  alt="profile2"
                  className="pre_questioner"
                />
              ) : (
                <img
                  src={spaceOwner.picture}
                  alt="profile2"
                  className="respondent"
                  onClick={() => {
                    if (spaceId !== received.answers[0].userId.toString()) {
                      window.location.href = `/${received.answers[0].userId}`;
                    }
                  }}
                />
              )}
            </div>
            <div className="cnt">
              {received.answers.length === 0 ? (
                <p className="pre_Nickname">{spaceOwner.name}</p>
              ) : (
                <p className="Nicname">{spaceOwner.name}</p>
              )}

              {received.answers.length === 0 ? (
                <UntilAnswering></UntilAnswering>
              ) : (
                <>
                  <p className="min">
                    {getTimeDifference(received.answers[0].createdTime)}
                  </p>
                  <AnonymousAnswer
                    question={received}
                    answers={received.answers}
                    currentUserInfo={currentUserInfo}
                  />
                </>
              )}

              {received.answers.length === 0 ? (
                ""
              ) : (
                <>
                  <div className="more">
                    {currentUserInfo.userId === received.answers[0].userId ? (
                      <img
                        src={More}
                        alt="more"
                        onClick={() => clickMore_1(index)}
                      />
                    ) : (
                      ""
                    )}

                    {a_deleteStates[index] && (
                      <div
                        className="del"
                        onClick={() =>
                          a_showDelModal(
                            received.answers[0].id,
                            spaceId,
                            currentUserInfo.userId
                          )
                        }
                      >
                        <p>
                          <img src={Bin} alt="btin" />
                          ÏÇ≠Ï†úÌïòÍ∏∞
                        </p>
                      </div>
                    )}
                  </div>
                </>
              )}
            </div>

            {/* ÏßàÎ¨∏ ÏÇ≠Ï†úÌïòÍ∏∞ ÌåùÏóÖ  */}
            {delModal && (
              <Delete
                questionId={selectedQuestionId}
                spaceId={selectedSpaceId} // Ïä§ÌéòÏù¥Ïä§ ID Ï†ÑÎã¨
                userId={selectedUserId} // Ïú†Ï†Ä ID Ï†ÑÎã¨
                onClose={onClose}
              ></Delete>
            )}
            {/* ÎãµÎ≥Ä ÏÇ≠Ï†úÌïòÍ∏∞ ÌåùÏóÖ  */}
            {a_delModal && (
              <ADelete
                answerId={selectedAnswerId}
                spaceId={selectedSpaceId} // Ïä§ÌéòÏù¥Ïä§ ID Ï†ÑÎã¨
                userId={selectedUserId} // Ïú†Ï†Ä ID Ï†ÑÎã¨
                onClose={onClose}
              ></ADelete>
            )}
            {/* -- Îì±Î°ùÎ∂àÍ∞Ä ÌåùÏóÖ */}
            {cantModal && <CantModal onClose={onClose}></CantModal>}
          </div>
        </React.Fragment>
      ))}
      {answerModal && (
        <AnswerRegister
          CloseAnswerModal={closeAnswerModal}
          currentUserInfo={currentUserInfo}
          questionId={selectedQuestionId}
          sentUserId={selectedQuestionUserId}
          sentUserPic={selectedQuestionUserPic}
          questionText={selectedQuestionText}
        ></AnswerRegister>
      )}
      {loading && <Loading />}
      <div ref={spinnerRef} />
    </>
  );
}

export default ReceiveComment;

// ÏßàÎ¨∏ Îì±Î°ù ÏãúÍ∞ÑÍ≥º ÌòÑÏû¨ ÏãúÍ∞Ñ ÏÇ¨Ïù¥Ïùò Ï∞®Ïù¥Î•º Í≥ÑÏÇ∞ÌïòÎäî Ìï®Ïàò
function getTimeDifference(createdTime) {
  return moment(createdTime).locale("ko").fromNow();
}
