import React, { useState } from "react";
import Close from "../../img/icon/close.png";
import { useParams } from "react-router-dom";
import { createAnswer } from "../../api/Q&A/createAnswer";
import PropTypes from 'prop-types';


function AnswerRegister({ CloseAnswerModal, currentUserInfo, questionId, sentUserId, sentUserPic, questionText }) {
  const { id } = useParams();
  let [inputCount, setInputCount] = useState(0);
  const [btn, setBtn] = useState(true);
  //ÎãµÎ≥Ä ÌÖçÏä§Ìä∏Î•º Ï†ÄÏû•ÌïòÍ∏∞ ÏúÑÌïú state Î≥ÄÏàò
  const [answerText, setAnswerText] = useState("");


  const onTextareaHandler = (e) => {
    setInputCount(e.target.value.length);
    setAnswerText(e.target.value);
  };
  const clickBtn = () => {
    setBtn(!btn);
  };

  // ÎãµÎ≥Ä Îì±Î°ùÌïòÎäî API
  const registerAnswer = async () => {
    try {
      await createAnswer(id, currentUserInfo, answerText, btn, questionId);
      CloseAnswerModal(); // ÏßàÎ¨∏ Îì±Î°ù ÌõÑ ÌåùÏóÖÏùÑ Îã´ÏäµÎãàÎã§.
      window.location.reload();
    } catch (error) {
      if (error.response && error.response.status === 401) {
        console.error("Î°úÍ∑∏Ïù∏Ïù¥ ÌïÑÏöîÌï©ÎãàÎã§.");
        // Î°úÍ∑∏Ïù∏Ïù¥ ÌïÑÏöîÌïú Í≤ΩÏö∞Ïóê ÎåÄÌïú Ï≤òÎ¶¨Î•º Ï∂îÍ∞ÄÌï©ÎãàÎã§.
      } else {
        console.error("ÎãµÎ≥Ä Îì±Î°ùÏóê Ïã§Ìå®Ìï®:", error);
        // ÌïÑÏöîÌïú ÏóêÎü¨ Ï≤òÎ¶¨ Î°úÏßÅÏùÑ Ï∂îÍ∞ÄÌï©ÎãàÎã§.
      }
    }
  };

  return (
    <div className="popupWrap">
      <div className="popup registerPopup">
        <div className="popupContainer">
          <div className="popupHeader">
            <img src={Close} alt="close" onClick={CloseAnswerModal} />
          </div>
          <div className="popupContent">
            <div className="profile que">
              <div>
                <img src={sentUserPic} alt="profile" className="questioner" />
              </div>
              <div>
                <p className="myId id">{sentUserId}</p>
                <p className="queCnt">
                  {questionText}
                </p>
              </div>
            </div>
            <div className="profile">
              <div>
                <img src={currentUserInfo.picture} alt="profile" className="respondent" />
              </div>
              <div>
                <p className="myId id">{currentUserInfo.name}</p>
                {btn ? (
                  <button className="openBtn" onClick={clickBtn}>
                    <span role="img" aria-label="link">
                      üîíÏßàÎ¨∏ÏûêÏóêÍ≤åÎßå Î≥¥Ïù¥Îäî ÎãµÎ≥Ä
                    </span>
                  </button>
                ) : (
                  <button className="openBtn" onClick={clickBtn}>
                     <span role="img" aria-label="link">
                    üì¢Í≥µÍ∞ú ÎãµÎ≥Ä
                    </span>
                  </button>
                )}
              </div>
            </div>
            <div className="text">
              <textarea maxLength="500" onChange={onTextareaHandler}></textarea>
            </div>
          </div>
          <div className="popupFooter">
            <p className="countNum">
              <span>{inputCount}</span>
              <span>/500</span>
            </p>
            <button className="btnSave" onClick={registerAnswer}>ÎÇ®Í∏∞Í∏∞</button>
          </div>
        </div>
      </div>
    </div>
  );
}

AnswerRegister.propTypes = {
  loseAnswerModal: PropTypes.any.isRequired,
  currentUserInfo: PropTypes.any.isRequired,
  questionId: PropTypes.any.isRequired,
  sentUserId: PropTypes.any.isRequired,
  sentUserPic: PropTypes.any.isRequired,
  questionText: PropTypes.any.isRequired,
  CloseAnswerModal: PropTypes.any.isRequired,
};


export default AnswerRegister;
