import { Link } from "react-router-dom";
import Comment from "../pages/Comment";
import React, { useState, useEffect } from "react";
import { getUserInfo } from "../api/getUserInfo";
import MobileBrowser from "../component/popup/MobileBrowser"; // ÌåùÏóÖÏ∞Ω Ïª¥Ìè¨ÎÑåÌä∏ ÏûÑÌè¨Ìä∏
import PropTypes from 'prop-types';

const Intro = ({ isLogin, setIsLogin }) => {
  const [currentUserInfo, setCurrentUserInfo] = useState({
    userId: "",
    picture: "",
    name: "",
  });



  const [showPopup, setShowPopup] = useState(false); // ÌåùÏóÖÏ∞Ω ÌëúÏãú Ïó¨Î∂Ä ÏÉÅÌÉú

  useEffect(() => {
    const initUserInfo = async () => {
      const response = await getUserInfo();
      if (response === false) {
        setIsLogin(false);
        return;
      } else {
        setCurrentUserInfo(response);
        setIsLogin(true);
      }
    };
    initUserInfo();

  }, [setIsLogin]);




  const handleConfirmPopup = () => {
    setShowPopup(false); // "ÎÑ§, Ïù¥ÎèôÌï©ÎãàÎã§." Î≤ÑÌäºÏùÑ ÎàÑÎ•¥Î©¥ ÌåùÏóÖÏ∞ΩÏùÑ Îã´ÏäµÎãàÎã§.
  };

  return (
    <div className="wrap intro">
      <div className="contentWrap">
        <p className="introTitle">
        <span role="img" aria-label="link">
          üêáÌÜ†ÎÅºüêáÎ°ú Î¨¥Î¨ºÏóê ÎÖπÏïÑ Îì§Ïûê
          </span></p>
        <Comment></Comment>
      
        {isLogin ? (
          <Link to={`/${currentUserInfo.userId}`} className="goSpace">
            <button className="space">Ïä§ÌéòÏù¥Ïä§ ÏûÖÏû•</button>
          </Link>
        ) : (
          <Link to="/login" className="goSpace">
            <button className="space">Ïä§ÌéòÏù¥Ïä§ ÏûÖÏû•</button>
          </Link>
        )}

          <div>
           <p className="inApp" onClick={() => setShowPopup(true)}>Îã§Î•∏ Ïï±ÏùÑ ÌÜµÌï¥ÏÑú Ï†ëÏÜçÌñàÎã§Î©¥</p>
            {showPopup && <MobileBrowser onClose={handleConfirmPopup} />}
          </div>

        <Link to="/policy" className="goPolicy">
          <p>PRIVACY POLICY</p>
        </Link>
      </div>
    </div>
  );
};

Intro.propTypes={
  isLogin: PropTypes.any.isRequired,
  setIsLogin: PropTypes.any.isRequired,  
}


export default Intro;
