    setDeleteStates("");
    setSelectedQuestionId(questionId); // ÏÑ†ÌÉùÌïú ÏßàÎ¨∏Ïùò IDÎ•º ÏÉÅÌÉúÍ∞íÏóê Ï†ÄÏû•
    setSelectedSpaceId(spaceId); // ÏÑ†ÌÉùÌïú ÏßàÎ¨∏Ïùò Ïä§ÌéòÏù¥Ïä§ IDÎ•º ÏÉÅÌÉúÍ∞íÏóê Ï†ÄÏû•
    setSelectedUserId(userId); // ÏÑ†ÌÉùÌïú ÏßàÎ¨∏Ïùò Ïú†Ï†Ä IDÎ•º ÏÉÅÌÉúÍ∞íÏóê Ï†ÄÏû•
    setDelModal(true);
  };

  // ÎãµÎ≥Ä ÏÇ≠Ï†úÌïòÍ∏∞ ÌÅ¥Î¶≠ Ïãú Î™®Îã¨ Ïò§Ìîà
  const a_showDelModal = (answerId, spaceId, userId) => {
    setSelectedAnswerId(answerId); // ÏÑ†ÌÉùÌïú ÏßàÎ¨∏Ïùò IDÎ•º ÏÉÅÌÉúÍ∞íÏóê Ï†ÄÏû•
    setSelectedSpaceId(spaceId); // ÏÑ†ÌÉùÌïú ÏßàÎ¨∏Ïùò Ïä§ÌéòÏù¥Ïä§ IDÎ•º ÏÉÅÌÉúÍ∞íÏóê Ï†ÄÏû•
    setSelectedUserId(userId); // ÏÑ†ÌÉùÌïú ÏßàÎ¨∏Ïùò Ïú†Ï†Ä IDÎ•º ÏÉÅÌÉúÍ∞íÏóê Ï†ÄÏû•
    a_setDelModal(true);
  };

  // ÎëêÎ≤àÏß∏ ÎãµÎ≥Ä Îì±Î°ù Ïãú Î™®Îã¨ Ïò§Ìîà
  const showCantModal = () => {
    setCantModal(true);
  };

  // ÏÇ≠Ï†ú ÌåùÏóÖ  Îã´Í∏∞
  const onClose = (e) => {
    setDelModal(false);
    a_setDelModal(false);
    setCantModal(false);
